package com.java;

public class PrintAtoZ {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=65;i<91;i++){
			char m =(char)i;
			System.out.print(m);
			
		}
		System.out.println();
		System.out.println("------------");
		for(int j=97;j<123;j++){
			char n=(char)j;
			System.out.print(n);
		}

	}

}
